import React from "react";
import { Link } from "react-router-dom";
import "../assets/css/sidebar.css";
import {} from "@fortawesome/free-solid-svg-icons";

const Sidebar = () => {
  const [familyDropdown, setFamilyDropdown] = React.useState(false);
  const [animalDropdown, setAnimalDropdown] = React.useState(false);
  
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>
          <i className="fas fa-paw"></i> Wildlife DB
        </h3>
      </div>
      <div className="sidebar-menu">
        <div className="menu-item">
           onMouseEnter={() => setIsShown(true)}
        onMouseLeave={() => setIsShown(false)}
          <button className="menu-btn active" onClick={() => setAnimalDropdown(!animalDropdown)}>
            <span>
              <i className="fas fa-dog"></i> Animals
            </span>
            <i className={`fas ${animalDropdown ? 'fa-chevron-up' : 'fa-chevron-down'} arrow`}></i>
          </button>


          {animalDropdown && (
          <div id="animals" className="dropdown-content show">
            <a href="#view-animals" className="dropdown-link">
              <i className="fas fa-list"></i> View Animals
            </a>
            <a href="#add-animal" className="dropdown-link">
              <i className="fas fa-plus-circle"></i> Add Animal
            </a>
          </div>
        )}
        </div>

        <div className="menu-item">
          <button className="menu-btn" onClick={() => setFamilyDropdown(!familyDropdown)}>
            <span>
              <i className="fas fa-users"></i> Families
            </span>
            <i className={`fas ${familyDropdown ? 'fa-chevron-down' : 'fa-chevron-up'} arrow`}></i>
          </button>

          {familyDropdown && (
          <div id="families" className="dropdown-content show">
            <a href="#view-families" className="dropdown-link">
              <i className="fas fa-list"></i> View Families
            </a>
            <a href="#add-family" className="dropdown-link">
              <i className="fas fa-plus-circle"></i> Add Family
            </a>
          </div>
        )}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
