import React from "react";

const Hero = () => {
    const overlayColor = 'rgba(0, 0, 0, 0.5)';
  return (
    <div
      className="hero-background d-flex align-items-center justify-content-center text-white text-center"
      style={{ 
        backgroundImage: `linear-gradient(${overlayColor}, ${overlayColor}), url(hero.jpg)`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh', 
      }}
    >
      <div className="container" style={{textAlign:"start"}}>

        <h1 className="display-4 fw-bold mb-3">Découvrez de près les plus grandes créatures du monde.</h1>

        <p className="mb-4 white">
          Discover new possibilities and embark on an unforgettable journey with us.
        </p>

        <button className="btn btn-primary">Get Started</button>
        <button className="btn btn-outline-primary mx-2">Learn More</button>
      </div>
    </div>
  );
};

export default Hero;
