import React, { useState,useEffect } from "react";
import api from "../../api";

function Filter() {
  const [continents, setContinents] = useState([]);
  const [families, setFamilies] = useState([]);

  useEffect(async() =>{
    try{api.get("/continents").then((res) => {
        setContinents(res.data);
      });}catch{

      }
      
      api.get("/family").then((res) => {
        setFamilies(res.data);
      });
    },
    []);

  return (
    <>
      <div style={{ width: "100%", backgroundColor: "lightgrey", display:"inline" }}>
        <form action="">
          <div>
            <label htmlFor="exampleSelect1" class="form-label mt-4">
              Famille
            </label>
            <select class="form-select" id="exampleSelect1">
              {families.map((family)=>{
                <option key={family.id}>{family.name}</option>
              })
              }
            </select>
          </div>

          <div>
            <label htmlFor="exampleSelect1" class="form-label mt-4">
              Continent
            </label>
            <select class="form-select" id="exampleSelect1">
              {continents.map((continent)=>{
                <option>{continent.name}</option>
              })}
            </select>
          </div>
        </form>
      </div>
    </>
  );
}

export default Filter;
