import React, { useState, useEffect } from "react";
import api from "../../api";

function Filter() {
  const [continents, setContinents] = useState([]);
  const [families, setFamilies] = useState([]);

  useEffect(() => {
    {
      api.get("/continents").then((res) => {
        setContinents(res.data);
      });
    }

    api.get("/family").then((res) => {
      setFamilies(res.data);
    });
  }, []);

  return (
    <>
      <form
        action=""
        style={{ display: "inline-flex", justifyContent: "flex-end" }}
      >
        <div className="d-flex">
          <label htmlFor="exampleSelect1" class="form-label ">
            Famille
          </label>
          <select >
            {families.map((family) => (
              <option key={family.id}>{family.name}</option>
            ))}
          </select>
        </div>

        <div className="d-flex">
          <label htmlFor="exampleSelect1" class="form-label">
            Continent
          </label>
          <select className="" >
            {continents.map((continent) => (
              <option>{continent.name}</option>
            ))}
          </select>
        </div>
      </form>
    </>
  );
}

export default Filter;
