import { CirclePlus } from "lucide-react";
import React from "react";
import api from "../../api";

function Card(props) {
  const cardStyle = {
  position: "relative",
  overflow: "hidden",
  color: "white",
  height: "18em",
  width: "12em",
  margin: "1em",
  borderRadius: ".8em",
};

const backgroundStyle = {
  position: "absolute",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundImage: `linear-gradient(
    rgba(0, 0, 0, 0.3), 
    rgba(0, 0, 0, 0.3)
  ), url(${props.img})`,
  backgroundSize: "cover",
  backgroundPosition: "center",
  transition: "transform 0.3s ease, filter 0.3s ease",
  zIndex: 1,
};

  return (
    <div className="col-sm-6 col-md-3 category-card d-flex flex-column justify-content-center p-3" key={props.index} style={cardStyle}>
      <div className="card border-0 shadow-sm h-100" style={{borderRadius:"0"}}>
        <img
          src={`http://127.0.0.1:8000/storage/${props.img}`}
          className="card-img-top"
          alt={props.name}
          style={{
            objectFit: "cover",
            height: "200px",
            borderRadius: "0",
            padding: "0"
          }}
        />
        <div className="card-body">
          <h5 className="card-title fw-bold mb-0">{props.name}</h5>
          {props.link && (
            <a onClick={props.onClick} className="stretched-link text-decoration-none text-success fw-semibold">
                Voir plus <CirclePlus size={16} />
            </a>
          )}
        </div>
      </div>

      

    </div>
  );
}

export default Card;
