import React, { useEffect, useState } from "react";
import CatalogueHero from "../../components/client/CatalogueHero";
import api from "../../api";
import Card from "../../components/client/Card";
import Footer from "../../components/client/Footer";
import Modal from "../../components/Modal";
import Loading from "../../components/Loading";
import { useSearchParams } from "react-router-dom";

const Catalogue = () => {
  const [animals, setAnimals] = React.useState([]);
  const [show, setShow] = React.useState(false);
  const [title, setTitle] = React.useState("");
  const [description, setDescription] = React.useState("");
  const [continents, setContinents] = React.useState([]);
  const [family, setFamily] = React.useState("");
  const [image, setImage] = React.useState("");
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [page, setPage] = useState(1);
  const [lastPage, setLastPage] = useState(1);
  const [searchParams] = useSearchParams();
  const search = searchParams.get("search") || "";

  useEffect(() => {
    setLoading(true);
    setAnimals("");
    if (search) {
      api
        .get(`/animals?search=${search}`)
        .then((response) => {
          setAnimals(response.data.data || response.data);
        })
        .catch((error) => {
          console.error("There was an error fetching the animals!", error);
          setError(error);
        })
        .finally(() => {
          setLoading(false);
        });
    } else {
      api
        .get("/animals")
        .then((response) => {
          setAnimals(response.data.data || response.data);
        })
        .catch((error) => {
          console.error("There was an error fetching the animals!", error);
          setError(error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [search]);

  return (
    <div>
      <section>
        <CatalogueHero />
      </section>
      <section>
        <div style={{ width: "100%", backgroundColor: "lightgrey" }}>
          <form action="">
            <div>
              <label for="exampleSelect1" class="form-label mt-4">
                Example select
              </label>
              <select class="form-select" id="exampleSelect1">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
            <div>
              <label for="exampleSelect1" class="form-label mt-4">
                Example select
              </label>
              <select class="form-select" id="exampleSelect1">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
          </form>
        </div>
      </section>

      <section id="catalogue">
        <div className="d-flex gap-4 m-5 flex-wrap justify-content-center">
          {animals &&
            animals.map((animal, index) => (
              <Card
                img={animal.image_path}
                key={index}
                name={animal.name}
                width="18rem"
                onClick={() => {
                  setTitle(animal.name);
                  setShow(true);
                  setDescription(animal.description);
                  setContinents(animal.continents);
                  setFamily(animal.family.name);
                  setImage(animal.image_path);
                }}
                link={true}
              ></Card>
            ))}
        </div>
      </section>
      <Modal Title={title} show={show} onClose={() => setShow(!show)}>
        <img
          src={`http://127.0.0.1:8000/storage/${image}`}
          width="600"
          alt={title}
        ></img>
        <h6>Description</h6>
        <p>{description}</p>
        <h6 className="mt-3">Famille</h6>
        <p>{family}</p>
        <h6>Continents</h6>

        {continents.map((continent, index) => (
          <span key={continent.id}>{continent.name}. </span>
        ))}
      </Modal>

      <Loading loading={loading} />
      {error && (
        <div className="alert alert-danger" role="alert">
          {error.message}
        </div>
      )}
      <Footer />
    </div>
  );
};

export default Catalogue;
