import React from "react";
import { Link } from "react-router-dom";
import "./Sidebar.css";
import "../assets/js/sidebar.js";

const Sidebar = () => {
  const [familyDropdown, setFamilyDropdown] = React.useState(false);
  const [animalDropdown, setAnimalDropdown] = React.useState(false);


  function toggleDropdown(id) {
    const dropdown = document.getElementById(id);
    const button = dropdown.previousElementSibling;

    // Close all other dropdowns
    document.querySelectorAll(".dropdown-content").forEach((content) => {
      if (content.id !== id && content.classList.contains("show")) {
        content.classList.remove("show");
        content.previousElementSibling.classList.remove("active");
      }
    });

    // Toggle current dropdown
    dropdown.classList.toggle("show");
    button.classList.toggle("active");
  }

  // Close dropdown when clicking a link
  document.querySelectorAll(".dropdown-link").forEach((link) => {
    link.addEventListener("click", function (e) {
      console.log("Navigating to:", this.getAttribute("href"));
    });
  });


  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>
          <i className="fas fa-paw"></i> Wildlife DB
        </h3>
      </div>
      <div className="sidebar-menu">
        <div className="menu-item">
          <button className="menu-btn" onClick={() => setAnimalDropdown("animals")}>
            <span>
              <i className="fas fa-dog"></i> Animals
            </span>
            <i className="fas fa-chevron-down arrow"></i>
          </button>
          <div id="animals" className="dropdown-content">
            <a href="#view-animals" className="dropdown-link">
              <i className="fas fa-list"></i> View Animals
            </a>
            <a href="#add-animal" className="dropdown-link">
              <i className="fas fa-plus-circle"></i> Add Animal
            </a>
          </div>
        </div>

        <div className="menu-item">
          <button className="menu-btn" onclick="toggleDropdown('families')">
            <span>
              <i className="fas fa-users"></i> Families
            </span>
            <i className="fas fa-chevron-down arrow"></i>
          </button>
          <div id="families" className="dropdown-content">
            <a href="#view-families" className="dropdown-link">
              <i className="fas fa-list"></i> View Families
            </a>
            <a href="#add-family" className="dropdown-link">
              <i className="fas fa-plus-circle"></i> Add Family
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
