import React from "react";

const Hero = () => {
  return (
    <div
      className="hero-background d-flex align-items-center justify-content-center text-white text-center"
      style={{ 
        backgroundImage: 'url(hero.jpg)', // The URL you provided
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '400px', // Example height, adjust as needed
      }}
    >
      <div className="container">
        {/* Bootstrap 'display' classes for large, prominent text */}
        <h1 className="display-4 fw-bold mb-3">Welcome to Our Adventure</h1>

        <p className="lead mb-4">
          Discover new possibilities and embark on an unforgettable journey with us.
        </p>
      
        <a href="#" className="btn btn-primary btn-lg">
          Explore Now
        </a>
      </div>
    </div>
  );
};

export default Hero;
