import React from "react";

const Hero = () => {
  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        backgroundImage: `url(hero.jpg)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        position: "relative",
        color: "#fff",
      }}
    >
      {/* ðŸ”² Dark overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.6)",
        }}
      ></div>

      {/* ðŸ§  Centered content */}
      <div
        style={{
          position: "relative",
          zIndex: 2,
          height: "100%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "0 20px",
        }}
      >
        <h1 className="display-4 fw-bold mb-3">Welcome to Our Zoo</h1>
        <p className="lead mb-4">
          Discover and manage all animals, families, and continents â€” powered by
          Laravel & React.
        </p>
        <a href="/dashboard" className="btn btn-primary btn-lg">
          Go to Dashboard
        </a>
      </div>
    </div>
  );
};

export default Hero;
