import React, { useEffect } from "react";
import CatalogueHero from "../../components/client/CatalogueHero";
import api from "../../api";
import Card from "../../components/client/Card";
import Footer from "../../components/client/Footer";

const Catalogue = () => {
  const [animals, setAnimals] = React.useState([]);
  const [show, setShow] = React.useState(false);
  const [title, setTitle] = React.useState("Ajouter un animal");

  useEffect(() => {
    api
      .get("/animals")
      .then((response) => {
        setAnimals(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("There was an error fetching the animals!", error);
      });
  }, []);
  return (
    <div>
      <section>
        <CatalogueHero />
      </section>

      <section id="catalogue">
        <div className="d-flex gap-4 m-5">
          {animals.map((animal, index) => (
            <Card
              key={index}
              name={animal.name}
              width="18rem"
              link={true}
            ></Card>
          ))}
        </div>
      </section>
      <Modal Title={title} show={false} onClose={() => setShow(!show)}></Modal>
      <Footer />
    </div>
  );
};

export default Catalogue;
